import React, { useState, useEffect } from 'react';
import { adminAPI } from '../../services/api';

const Dashboard = () => {
  const [dashboardData, setDashboardData] = useState(null);
  const [visitors, setVisitors] = useState([]);
  const [loading, setLoading] = useState(true);
  const [filters, setFilters] = useState({
    startDate: new Date().toISOString().split('T')[0],
    endDate: new Date().toISOString().split('T')[0],
    status: 'all',
    search: ''
  });
  const [currentPage, setCurrentPage] = useState(1);
  const [selectedImage, setSelectedImage] = useState(null);
  
  // Security Emails State
  const [showSecurityEmails, setShowSecurityEmails] = useState(false);
  const [securityEmails, setSecurityEmails] = useState(['']);
  const [securityLoading, setSecurityLoading] = useState(false);
  const [securityMessage, setSecurityMessage] = useState('');

  // Load TODAY'S data automatically on component mount
  useEffect(() => {
    fetchDashboardData();
    fetchVisitors();
  }, []);

  // Fetch data when page changes
  useEffect(() => {
    fetchVisitors();
  }, [currentPage]);

  const fetchDashboardData = async () => {
    try {
      const response = await adminAPI.getDashboard();
      setDashboardData(response.data.data);
    } catch (error) {
      console.error('Error fetching dashboard:', error);
    }
  };

  const fetchVisitors = async () => {
    setLoading(true);
    try {
      const params = {
        ...filters,
        page: currentPage,
        limit: 10
      };
      console.log('ðŸ“Š Fetching visitors with:', params);
      const response = await adminAPI.getVisitors(params);
      setVisitors(response.data.data);
    } catch (error) {
      console.error('Error fetching visitors:', error);
    } finally {
      setLoading(false);
    }
  };

  // Security Emails Functions
  const addSecurityEmailField = () => {
    setSecurityEmails([...securityEmails, '']);
  };

  const handleSecurityEmailChange = (index, value) => {
    const newEmails = [...securityEmails];
    newEmails[index] = value;
    setSecurityEmails(newEmails);
  };

  const removeSecurityEmailField = (index) => {
    const newEmails = securityEmails.filter((_, i) => i !== index);
    setSecurityEmails(newEmails);
  };

  // âœ… FIXED: Working security emails function
  const handleSecuritySubmit = async (e) => {
    e.preventDefault();
    setSecurityLoading(true);
    setSecurityMessage('');

    try {
      const validEmails = securityEmails.filter(email => email.trim() !== '');
      
      if (validEmails.length === 0) {
        setSecurityMessage('Please add at least one security email');
        return;
      }

      console.log('ðŸ”„ Sending security emails:', validEmails);

      // âœ… WORKING: Use direct fetch (proven to work)
      const response = await fetch('http://localhost:5000/api/admin/add-security-emails', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          securityEmails: validEmails
        })
      });

      const result = await response.json();
      console.log('ðŸ”„ Backend response:', result);

      if (response.ok) {
        setSecurityMessage('âœ… Security emails added successfully!');
        setSecurityEmails(['']);
        setShowSecurityEmails(false);
      } else {
        setSecurityMessage('âŒ ' + (result.message || 'Failed to add security emails'));
      }
      
    } catch (error) {
      console.error('âŒ Error:', error);
      setSecurityMessage('âŒ Network error - check console');
    } finally {
      setSecurityLoading(false);
    }
  };

  // Your existing functions remain the same...
  const handleFilterChange = (key, value) => {
    setFilters(prev => ({
      ...prev,
      [key]: value
    }));
  };

  const handleSearch = () => {
    setCurrentPage(1);
    fetchVisitors();
  };

  const handleReset = () => {
    const today = new Date().toISOString().split('T')[0];
    setFilters({
      startDate: today,
      endDate: today,
      status: 'all',
      search: ''
    });
    setCurrentPage(1);
  };

  const handleExport = async (format) => {
    try {
      const response = await adminAPI.exportVisitors({
        ...filters,
        format
      });
      
      const blob = new Blob([response.data], { type: format === 'csv' ? 'text/csv' : 'application/json' });
      const url = window.URL.createObjectURL(blob);
      const link = document.createElement('a');
      link.href = url;
      link.download = `visitors-${filters.startDate}-to-${filters.endDate}.${format}`;
      link.click();
      window.URL.revokeObjectURL(url);
    } catch (error) {
      console.error('Export error:', error);
    }
  };

  const getStatusColor = (status) => {
    switch (status) {
      case 'approved': return '#28a745';
      case 'pending': return '#ffc107';
      case 'rejected': return '#dc3545';
      default: return '#6c757d';
    }
  };

  if (!dashboardData) {
    return <div style={styles.loading}>Loading Dashboard...</div>;
  }

  return (
    <div style={styles.container}>
      {/* Image Modal */}
      {selectedImage && (
        <div style={styles.modalOverlay} onClick={() => setSelectedImage(null)}>
          <div style={styles.modalContent} onClick={(e) => e.stopPropagation()}>
            <button 
              style={styles.closeButton}
              onClick={() => setSelectedImage(null)}
            >
              âœ•
            </button>
            <img 
              src={selectedImage} 
              alt="Visitor" 
              style={styles.modalImage}
            />
          </div>
        </div>
      )}

      {/* Security Emails Modal */}
      {showSecurityEmails && (
        <div style={styles.modalOverlay} onClick={() => setShowSecurityEmails(false)}>
          <div style={styles.modalContent} onClick={(e) => e.stopPropagation()}>
            <button 
              style={styles.closeButton}
              onClick={() => setShowSecurityEmails(false)}
            >
              âœ•
            </button>
            <h3>Add Security Emails</h3>
            <p>Add email addresses that will be allowed to register as Security staff.</p>
            
            {securityMessage && (
              <div style={securityMessage.includes('successfully') ? styles.success : styles.error}>
                {securityMessage}
              </div>
            )}

            <form onSubmit={handleSecuritySubmit}>
              {securityEmails.map((email, index) => (
                <div key={index} style={styles.emailRow}>
                  <input
                    type="email"
                    placeholder="security@example.com"
                    value={email}
                    onChange={(e) => handleSecurityEmailChange(index, e.target.value)}
                    style={styles.emailInput}
                    required
                  />
                  {securityEmails.length > 1 && (
                    <button
                      type="button"
                      onClick={() => removeSecurityEmailField(index)}
                      style={styles.removeBtn}
                    >
                      Remove
                    </button>
                  )}
                </div>
              ))}
              
              <div style={styles.buttonGroup}>
                <button
                  type="button"
                  onClick={addSecurityEmailField}
                  style={styles.addBtn}
                >
                  + Add Another Email
                </button>
                
                <button
                  type="submit"
                  disabled={securityLoading}
                  style={styles.submitBtn}
                >
                  {securityLoading ? 'Adding Emails...' : 'Save Security Emails'}
                </button>
              </div>
            </form>
          </div>
        </div>
      )}

      <div style={styles.header}>
        <h2>Admin Dashboard</h2>
        <div style={styles.headerActions}>
          <button 
            onClick={() => setShowSecurityEmails(true)}
            style={styles.securityBtn}
          >
            Add Security Emails
          </button>
          <button 
            onClick={() => handleExport('csv')}
            style={styles.exportBtn}
          >
            Export CSV
          </button>
          <button 
            onClick={() => handleExport('json')}
            style={styles.exportBtn}
          >
            Export JSON
          </button>
          <button 
            onClick={() => {
              localStorage.clear();
              window.location.href = '/login';
            }}
            style={styles.logoutBtn}
          >
            Logout
          </button>
        </div>
      </div>

      {/* Stats Cards */}
      <div style={styles.statsContainer}>
        <div style={styles.statCard}>
          <h3 style={styles.statNumber}>{dashboardData.stats.total}</h3>
          <p style={styles.statLabel}>Total Today</p>
        </div>
        <div style={styles.statCard}>
          <h3 style={styles.statNumber}>{dashboardData.stats.pending}</h3>
          <p style={styles.statLabel}>Pending</p>
        </div>
        <div style={styles.statCard}>
          <h3 style={styles.statNumber}>{dashboardData.stats.approved}</h3>
          <p style={styles.statLabel}>Approved</p>
        </div>
        <div style={styles.statCard}>
          <h3 style={styles.statNumber}>{dashboardData.stats.rejected}</h3>
          <p style={styles.statLabel}>Rejected</p>
        </div>
        <div style={styles.statCard}>
          <h3 style={styles.statNumber}>{dashboardData.stats.currentHour}</h3>
          <p style={styles.statLabel}>This Hour</p>
        </div>
      </div>

      {/* Filters with SEARCH button */}
      <div style={styles.filters}>
        <div style={styles.filterGroup}>
          <label>Start Date</label>
          <input
            type="date"
            value={filters.startDate}
            onChange={(e) => handleFilterChange('startDate', e.target.value)}
            style={styles.filterInput}
          />
        </div>
        <div style={styles.filterGroup}>
          <label>End Date</label>
          <input
            type="date"
            value={filters.endDate}
            onChange={(e) => handleFilterChange('endDate', e.target.value)}
            style={styles.filterInput}
          />
        </div>
        <div style={styles.filterGroup}>
          <label>Status</label>
          <select
            value={filters.status}
            onChange={(e) => handleFilterChange('status', e.target.value)}
            style={styles.filterInput}
          >
            <option value="all">All Status</option>
            <option value="pending">Pending</option>
            <option value="approved">Approved</option>
            <option value="rejected">Rejected</option>
          </select>
        </div>
        <div style={styles.filterGroup}>
          <label>Search</label>
          <input
            type="text"
            placeholder="Search by name, phone, flat..."
            value={filters.search}
            onChange={(e) => handleFilterChange('search', e.target.value)}
            style={styles.filterInput}
          />
        </div>
        
        {/* SEARCH & RESET BUTTONS */}
        <div style={styles.filterGroup}>
          <label>&nbsp;</label>
          <div style={styles.buttonGroup}>
            <button 
              onClick={handleSearch}
              style={styles.searchButton}
            >
              ðŸ” Search
            </button>
            <button 
              onClick={handleReset}
              style={styles.resetButton}
            >
              ðŸ”„ Reset
            </button>
          </div>
        </div>
      </div>

      {/* Visitors Table */}
      <div style={styles.tableContainer}>
        <div style={styles.tableHeader}>
          <h3>
            {filters.startDate && filters.endDate 
              ? `Visitors: From ${filters.startDate} to ${filters.endDate}` 
              : "Today's Visitors"}
            <small style={{color: '#6c757d', fontSize: '0.8rem', marginLeft: '1rem'}}>
              (Showing: {visitors.length} records)
            </small>
          </h3>
          <div style={styles.pagination}>
            <button 
              onClick={() => setCurrentPage(prev => Math.max(1, prev - 1))}
              disabled={currentPage === 1}
              style={styles.pageButton}
            >
              â† Previous
            </button>
            <span style={styles.pageInfo}>Page {currentPage}</span>
            <button 
              onClick={() => setCurrentPage(prev => prev + 1)}
              disabled={visitors.length < 10}
              style={styles.pageButton}
            >
              Next â†’
            </button>
          </div>
        </div>

        {loading ? (
          <div style={styles.loading}>Loading visitors...</div>
        ) : (
          <>
            <table style={styles.table}>
              <thead>
                <tr>
                  <th style={styles.th}>Visitor Image</th>
                  <th style={styles.th}>Date & Time</th>
                  <th style={styles.th}>Visitor Name</th>
                  <th style={styles.th}>Phone</th>
                  <th style={styles.th}>Owner</th>
                  <th style={styles.th}>Flat</th>
                  <th style={styles.th}>Bike No</th>
                  <th style={styles.th}>Status</th>
                  <th style={styles.th}>Security</th>
                </tr>
              </thead>
              <tbody>
                {visitors.map((visitor) => (
                  <tr key={visitor._id} style={styles.tr}>
                    <td style={styles.td}>
                      {visitor.visitorImage ? (
                        <img 
                          src={`${process.env.REACT_APP_API_URL || 'http://localhost:5000'}/uploads/${visitor.visitorImage}`}
                          alt="Visitor"
                          style={styles.visitorImage}
                          onClick={() => setSelectedImage(`${process.env.REACT_APP_API_URL || 'http://localhost:5000'}/uploads/${visitor.visitorImage}`)}
                          onError={(e) => {
                            e.target.style.display = 'none';
                            e.target.nextSibling.style.display = 'block';
                          }}
                        />
                      ) : (
                        <span style={styles.noImage}>No Image</span>
                      )}
                    </td>
                    <td style={styles.td}>
                      {new Date(visitor.createdAt).toLocaleString()}
                    </td>
                    <td style={styles.td}>{visitor.visitorName}</td>
                    <td style={styles.td}>{visitor.visitorPhone}</td>
                    <td style={styles.td}>{visitor.ownerName}</td>
                    <td style={styles.td}>Flat {visitor.flatNo}, {visitor.floor}</td>
                    <td style={styles.td}>{visitor.bikeNumber || '-'}</td>
                    <td style={styles.td}>
                      <span style={{
                        ...styles.status,
                        backgroundColor: getStatusColor(visitor.status)
                      }}>
                        {visitor.status.toUpperCase()}
                      </span>
                    </td>
                    <td style={styles.td}>
                      {visitor.capturedBy?.name || 'Unknown'}
                    </td>
                  </tr>
                ))}
              </tbody>
            </table>
            
            {visitors.length === 0 && (
              <div style={styles.noData}>
                No visitors found for selected criteria
              </div>
            )}
          </>
        )}
      </div>

      {/* Last Updated */}
      <div style={styles.lastUpdated}>
        Last Updated: {new Date(dashboardData.lastUpdated).toLocaleString()}
      </div>
    </div>
  );
};

