    borderRadius: '6px',
    border: '1px solid rgba(245, 158, 11, 0.3)',
    '@media (max-width: 768px)': {
      padding: '0.4rem 0.6rem',
      gap: '0.25rem'
    }
  },
  spinner: {
    width: '14px',
    height: '14px',
    border: '2px solid transparent',
    borderTop: '2px solid #f59e0b',
    borderRadius: '50%',
    animation: 'spin 1s linear infinite',
    '@media (max-width: 768px)': {
