import React, { useState, useRef } from 'react';
import Webcam from 'react-webcam';
import { visitorAPI, securityAPI } from '../../services/api'; // ADD securityAPI

const VisitorForm = () => {
  const [formData, setFormData] = useState({
    visitorName: '',
    visitorPhone: '',
    visitorAddress: '',
    visitorEmail: '',
    ownerName: '',
    ownerEmail: '',
    ownerMobile: '',
    flatNo: '',
    floor: '',
    bikeNumber: ''
  });
  const [visitorImage, setVisitorImage] = useState(null);
  const [bikeImage, setBikeImage] = useState(null);
  const [showVisitorCamera, setShowVisitorCamera] = useState(false);
  const [showBikeCamera, setShowBikeCamera] = useState(false);
  const [loading, setLoading] = useState(false);
  const [message, setMessage] = useState('');
  // ADD THESE NEW STATES
  const [detectedBikeNumber, setDetectedBikeNumber] = useState('');
  const [isDetecting, setIsDetecting] = useState(false);

  const visitorWebcamRef = useRef(null);
  const bikeWebcamRef = useRef(null);

  const handleChange = (e) => {
    setFormData({
      ...formData,
      [e.target.name]: e.target.value
    });
  };

  const captureVisitorPhoto = () => {
    const imageSrc = visitorWebcamRef.current.getScreenshot();
    setVisitorImage(imageSrc);
    setShowVisitorCamera(false);
  };

  // UPDATED: Add bike number detection after capture
  const captureBikePhoto = () => {
    const imageSrc = bikeWebcamRef.current.getScreenshot();
    setBikeImage(imageSrc);
    setShowBikeCamera(false);
    
    // AUTO-DETECT BIKE NUMBER IMMEDIATELY
    detectBikeNumber(imageSrc);
  };

  // NEW FUNCTION: Detect bike number and show it immediately
  const detectBikeNumber = async (imageData) => {
    setIsDetecting(true);
    setDetectedBikeNumber('');
    
    try {
      console.log('ðŸ–¼ï¸ Detecting bike number...');
      
      const response = await securityAPI.detectBikeNumber({
        image: imageData
      });
      
      if (response.data.success && response.data.bike_number && response.data.bike_number !== '-') {
        const detectedNumber = response.data.bike_number;
        console.log('âœ… Detected:', detectedNumber);
        
        // Show detected number to security guard
        setDetectedBikeNumber(detectedNumber);
        
        // Auto-fill the bike number field (guard can change if wrong)
        setFormData(prev => ({
          ...prev,
          bikeNumber: detectedNumber
        }));
      } else {
        setDetectedBikeNumber('Not detected');
        console.log('âŒ No bike number detected');
      }
    } catch (error) {
      console.error('âŒ Detection failed:', error);
      setDetectedBikeNumber('Detection failed');
    } finally {
      setIsDetecting(false);
    }
  };

  // NEW: Handle manual bike number input (clear detection)
  const handleBikeNumberChange = (e) => {
    setFormData(prev => ({
      ...prev,
      bikeNumber: e.target.value
    }));
    // Clear detected number if user types manually
    if (e.target.value !== detectedBikeNumber) {
      setDetectedBikeNumber('');
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    setMessage('');

    try {
      const submitData = new FormData();
      
      // Add form data
      Object.keys(formData).forEach(key => {
        submitData.append(key, formData[key]);
      });

      // Convert base64 images to files
      if (visitorImage) {
        const visitorFile = base64ToFile(visitorImage, 'visitor.jpg');
        submitData.append('visitorImage', visitorFile);
      }

      if (bikeImage) {
        const bikeFile = base64ToFile(bikeImage, 'bike.jpg');
        submitData.append('bikeNumberImage', bikeFile);
      }

      await visitorAPI.create(submitData);
      setMessage('Visitor registered successfully! Email sent to owner.');
      
      // Reset form
      setFormData({
        visitorName: '',
        visitorPhone: '',
        visitorAddress: '',
        visitorEmail: '',
        ownerName: '',
        ownerEmail: '',
        ownerMobile: '',
        flatNo: '',
        floor: '',
        bikeNumber: ''
      });
      setVisitorImage(null);
      setBikeImage(null);
      setDetectedBikeNumber(''); // CLEAR detection
    } catch (error) {
      setMessage(error.response?.data?.message || 'Error submitting form');
    } finally {
      setLoading(false);
    }
  };

  const base64ToFile = (base64, filename) => {
    const arr = base64.split(',');
    const mime = arr[0].match(/:(.*?);/)[1];
    const bstr = atob(arr[1]);
    let n = bstr.length;
    const u8arr = new Uint8Array(n);
    
    while (n--) {
      u8arr[n] = bstr.charCodeAt(n);
    }
    
    return new File([u8arr], filename, { type: mime });
  };

  return (
    <div style={styles.container}>
      <div style={styles.header}>
        <h2>Visitor Registration</h2>
        <button 
          onClick={() => {
            localStorage.clear();
            window.location.href = '/login';
          }}
          style={styles.logoutBtn}
        >
          Logout
        </button>
      </div>

      {message && (
        <div style={styles.message}>
          {message}
        </div>
      )}

      <form onSubmit={handleSubmit} style={styles.form}>
        <div style={styles.section}>
          <h3>Visitor Details</h3>
          <div style={styles.row}>
            <div style={styles.inputGroup}>
              <label>Visitor Name *</label>
              <input
                type="text"
                name="visitorName"
                value={formData.visitorName}
                onChange={handleChange}
                required
                style={styles.input}
              />
            </div>
            <div style={styles.inputGroup}>
              <label>Phone Number *</label>
              <input
                type="tel"
                name="visitorPhone"
                value={formData.visitorPhone}
                onChange={handleChange}
                required
                style={styles.input}
              />
            </div>
          </div>

          <div style={styles.inputGroup}>
            <label>Address *</label>
            <textarea
              name="visitorAddress"
              value={formData.visitorAddress}
              onChange={handleChange}
              required
              style={styles.textarea}
            />
          </div>

          <div style={styles.inputGroup}>
            <label>Email</label>
            <input
              type="email"
              name="visitorEmail"
              value={formData.visitorEmail}
              onChange={handleChange}
              style={styles.input}
            />
          </div>

          {/* Visitor Photo Capture */}
          <div style={styles.cameraSection}>
            <label>Visitor Photo *</label>
            {!showVisitorCamera ? (
              <div>
                {visitorImage ? (
                  <div>
                    <img src={visitorImage} alt="Visitor" style={styles.previewImage} />
                    <button 
                      type="button" 
                      onClick={() => setShowVisitorCamera(true)}
                      style={styles.cameraBtn}
                    >
                      Retake Photo
                    </button>
                  </div>
                ) : (
                  <button 
                    type="button" 
                    onClick={() => setShowVisitorCamera(true)}
                    style={styles.cameraBtn}
                  >
                    Capture Visitor Photo
                  </button>
                )}
              </div>
            ) : (
              <div>
                <Webcam
                  audio={false}
                  ref={visitorWebcamRef}
                  screenshotFormat="image/jpeg"
                  style={styles.webcam}
                />
                <button 
                  type="button" 
                  onClick={captureVisitorPhoto}
                  style={styles.captureBtn}
                >
                  Capture Photo
                </button>
              </div>
            )}
          </div>
        </div>

        <div style={styles.section}>
          <h3>Owner & Property Details</h3>
          <div style={styles.row}>
            <div style={styles.inputGroup}>
              <label>Owner Name *</label>
              <input
                type="text"
                name="ownerName"
                value={formData.ownerName}
                onChange={handleChange}
                required
                style={styles.input}
              />
            </div>
            <div style={styles.inputGroup}>
              <label>Owner Email *</label>
              <input
                type="email"
                name="ownerEmail"
                value={formData.ownerEmail}
                onChange={handleChange}
                required
                style={styles.input}
              />
            </div>
          </div>

          <div style={styles.row}>
            <div style={styles.inputGroup}>
              <label>Owner Mobile *</label>
              <input
                type="tel"
                name="ownerMobile"
                value={formData.ownerMobile}
                onChange={handleChange}
                required
                style={styles.input}
              />
            </div>
            <div style={styles.inputGroup}>
              <label>Flat No *</label>
              <input
                type="text"
                name="flatNo"
                value={formData.flatNo}
                onChange={handleChange}
                required
                style={styles.input}
              />
            </div>
            <div style={styles.inputGroup}>
              <label>Floor *</label>
              <input
                type="text"
                name="floor"
                value={formData.floor}
                onChange={handleChange}
                required
                style={styles.input}
              />
            </div>
          </div>
        </div>

        <div style={styles.section}>
          <h3>Vehicle Details (Optional)</h3>
          
          {/* ADD: Detected Bike Number Display */}
          {detectedBikeNumber && (
            <div style={styles.detectionResult}>
              <strong>ðŸš— Detected Bike Number: </strong>
              <span style={styles.detectedNumber}>{detectedBikeNumber}</span>
              <div style={styles.detectionNote}>
                âœ“ Auto-detected from image. Verify and edit if needed.
              </div>
            </div>
          )}
          
          <div style={styles.inputGroup}>
            <label>Bike Number</label>
            <input
              type="text"
              name="bikeNumber"
              value={formData.bikeNumber}
              onChange={handleBikeNumberChange} // UPDATED
              placeholder={isDetecting ? "Detecting..." : "Enter bike number"}
              style={styles.input}
            />
          </div>

          {/* Bike Number Photo Capture */}
          <div style={styles.cameraSection}>
            <label>Bike Number Photo</label>
            {!showBikeCamera ? (
              <div>
                {bikeImage ? (
                  <div>
                    <img src={bikeImage} alt="Bike" style={styles.previewImage} />
                    <div style={styles.buttonGroup}>
                      <button 
                        type="button" 
                        onClick={() => setShowBikeCamera(true)}
                        style={styles.cameraBtn}
                      >
                        Retake Photo
                      </button>
                      {isDetecting && (
                        <span style={styles.detectingText}>ðŸ”„ Detecting...</span>
                      )}
                    </div>
                  </div>
                ) : (
                  <button 
                    type="button" 
                    onClick={() => setShowBikeCamera(true)}
                    style={styles.cameraBtn}
                  >
                    Capture Bike Number
                  </button>
                )}
              </div>
            ) : (
              <div>
                <Webcam
                  audio={false}
                  ref={bikeWebcamRef}
                  screenshotFormat="image/jpeg"
                  style={styles.webcam}
                />
                <button 
                  type="button" 
                  onClick={captureBikePhoto}
                  style={styles.captureBtn}
                >
                  Capture Number
                </button>
              </div>
            )}
          </div>
        </div>

        <button 
          type="submit" 
          disabled={loading || !visitorImage}
          style={styles.submitBtn}
        >
          {loading ? 'Submitting...' : 'Submit Visitor Form'}
        </button>
      </form>
    </div>
  );
};

const styles = {
  container: {
    padding: '2rem',
    maxWidth: '800px',
    margin: '0 auto'
  },
  header: {
    display: 'flex',
    justifyContent: 'space-between',
    alignItems: 'center',
    marginBottom: '2rem'
  },
  logoutBtn: {
    padding: '0.5rem 1rem',
    backgroundColor: '#dc3545',
    color: 'white',
    border: 'none',
    borderRadius: '4px',
    cursor: 'pointer'
  },
  message: {
    padding: '1rem',
    backgroundColor: '#d4edda',
    color: '#155724',
    borderRadius: '4px',
    marginBottom: '1rem'
  },
  form: {
    display: 'flex',
    flexDirection: 'column',
    gap: '2rem'
  },
  section: {
    border: '1px solid #ddd',
    padding: '1.5rem',
    borderRadius: '8px'
  },
  row: {
    display: 'flex',
    gap: '1rem',
    marginBottom: '1rem'
  },
  inputGroup: {
    flex: 1,
    display: 'flex',
    flexDirection: 'column'
  },
  label: {
    marginBottom: '0.5rem',
    fontWeight: 'bold'
  },
  input: {
    padding: '0.75rem',
    border: '1px solid #ddd',
    borderRadius: '4px',
    fontSize: '1rem'
  },
  textarea: {
    padding: '0.75rem',
    border: '1px solid #ddd',
    borderRadius: '4px',
    fontSize: '1rem',
    minHeight: '80px',
    resize: 'vertical'
  },
  cameraSection: {
    marginTop: '1rem'
  },
  cameraBtn: {
    padding: '0.75rem 1.5rem',
    backgroundColor: '#28a745',
    color: 'white',
    border: 'none',
    borderRadius: '4px',
    cursor: 'pointer',
    marginRight: '1rem'
  },
  captureBtn: {
    padding: '0.75rem 1.5rem',
    backgroundColor: '#007bff',
    color: 'white',
    border: 'none',
    borderRadius: '4px',
    cursor: 'pointer',
    marginTop: '1rem'
  },
  webcam: {
    width: '100%',
    maxWidth: '400px',
    borderRadius: '8px'
  },
  previewImage: {
    width: '200px',
    height: '150px',
    objectFit: 'cover',
    borderRadius: '8px',
    marginBottom: '1rem'
  },
  submitBtn: {
    padding: '1rem 2rem',
    backgroundColor: '#007bff',
    color: 'white',
    border: 'none',
    borderRadius: '4px',
    fontSize: '1.1rem',
    cursor: 'pointer',
    marginTop: '1rem'
  },
  // ADD THESE NEW STYLES:
  detectionResult: {
    backgroundColor: '#e8f5e8',
    padding: '1rem',
    borderRadius: '8px',
    marginBottom: '1rem',
    border: '2px solid #28a745'
  },
  detectedNumber: {
    fontSize: '1.2rem',
    fontWeight: 'bold',
    color: '#155724'
  },
  detectionNote: {
    fontSize: '0.9rem',
    color: '#28a745',
    marginTop: '0.5rem'
  },
  detectingText: {
    color: '#ffc107',
    fontWeight: 'bold'
  },
  buttonGroup: {
    display: 'flex',
    alignItems: 'center',
    gap: '1rem'
  }
};

export default VisitorForm;
